function renderProducts(e){$(".products .row").empty(),e.length?e.map(e=>{$(`\n            <div class="col-sm-4">\n                        <div class="item">\n                            <img src=${e.img} alt="">\n                            <h2>${e.name}</h2>\n                            <span>${e.price}$</span>\n                        </div>\n                    </div>\n            `).appendTo(".products .row")}):$("<h1>No Products Found! </h1>").appendTo(".products .row")}$((function(){const e=[{id:1,name:"Apple",price:5,categories:["Man"],img:"https://salt.tikicdn.com/cache/280x280/ts/product/36/bd/31/38cfa79d6213fb948a13ad20b0b9d617.jpg"},{id:2,name:"Samsung",price:20,categories:["Man","Woman"],img:"https://salt.tikicdn.com/cache/280x280/ts/product/36/bd/31/38cfa79d6213fb948a13ad20b0b9d617.jpg"},{id:3,name:"Viettel",price:15,categories:["Man","Woman","Release"],img:"https://salt.tikicdn.com/cache/280x280/ts/product/36/bd/31/38cfa79d6213fb948a13ad20b0b9d617.jpg"}];renderProducts(e),$(".search-item").keyup((function(t){t.preventDefault();const a=$(".search-item").val();renderProducts(e.filter(e=>e.name.toLowerCase().includes(a.toLowerCase())).map(e=>({...e,name:e.name.split(a).join(`<span style="color: red">${a}</span>`)})))})),$(".filter-item").change((function(t){t.preventDefault();switch($(this).val()){case"":renderProducts(e);break;case"greater10":renderProducts(e.filter(e=>e.price>=10));break;case"lesser10":renderProducts(e.filter(e=>e.price<=10))}}))}));