$((function(){let t=[],n=[];$.get("https://res.cloudinary.com/sivadass/raw/upload/v1535817394/json/products.json",(function(n){t=n,n.map(t=>{$(`<div class="col-sm-4">\n             <div class="item" data-id=${t.id}>\n                 <img src=${t.image} alt="">\n                 <span>Price:$ ${t.price} </span>\n                 <button>Add to cart</button>\n             </div>\n         </div>`).appendTo(".products .row")})})),$("body").on("click","button",(function(){const i=$(this).parents(".item").data("id"),s=t.find(t=>t.id===i);n.push(s),localStorage.setItem("cart",JSON.stringify(n))}))}));